require_relative "./test_helper"

class Q5LongestPalindromicSubstringTest < Minitest::Test
  include Q5LongestPalindromicSubstring

  def test_it_works
    s = "babad"
    assert_equal "aba", longest_palindrome(s)

    s = "cbbd"
    assert_equal "bb", longest_palindrome(s)

    s = ""
    v = longest_palindrome(s)
    puts v
  end
end
